        { "cloud-init", @"#cloud-config
apt-update: true
apt-upgrade: true
" },
        { "foo", "bar" },
    };
    var mainServer = new Scaleway.Instance.Server("mainServer", new()
    {
        Image = "ubuntu_focal",
        Type = "DEV1-S",
    });

    // User data with a single value
    var mainUserData = new Scaleway.Instance.UserData("mainUserData", new()
    {
        ServerId = mainServer.Id,
        Key = "foo",
        Value = "bar",
    });

    // User Data with many keys.
    var data = new List<Scaleway.Instance.UserData>();
    for (var rangeIndex = 0; rangeIndex < userData; rangeIndex++)
    {
        var range = new { Value = rangeIndex };
        data.Add(new Scaleway.Instance.UserData($"data-{range.Value}", new()
        {
            ServerId = mainServer.Id,
            Key = range.Key,
            Value = range.Value,
        }));
    }
});
