public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        var mainPublicGatewayIP = new PublicGatewayIP("mainPublicGatewayIP");

        var mainPrivateNetwork = new PrivateNetwork("mainPrivateNetwork");

        var mainPublicGateway = new PublicGateway("mainPublicGateway", PublicGatewayArgs.builder()        
            .type("VPC-GW-S")
            .ipId(mainPublicGatewayIP.id())
            .build());

        var mainPublicGatewayDHCP = new PublicGatewayDHCP("mainPublicGatewayDHCP", PublicGatewayDHCPArgs.builder()        
            .subnet("10.0.0.0/24")
            .build());

        var mainGatewayNetwork = new GatewayNetwork("mainGatewayNetwork", GatewayNetworkArgs.builder()        
            .gatewayId(mainPublicGateway.id())
            .privateNetworkId(mainPrivateNetwork.id())
            .dhcpId(mainPublicGatewayDHCP.id())
            .cleanupDhcp(true)
            .enableMasquerade(true)
            .build());

        var mainServer = new Server("mainServer", ServerArgs.builder()        
            .type("DEV1-S")
            .image("debian_bullseye")
            .enableIpv6(false)
            .privateNetworks(ServerPrivateNetworkArgs.builder()
                .pnId(mainPrivateNetwork.id())
                .build())
            .build());

        var mainIP = new IP("mainIP");

        var mainVpc_privateNetworkPrivateNetwork = new PrivateNetwork("mainVpc/privateNetworkPrivateNetwork");

        var mainLoadBalancer = new LoadBalancer("mainLoadBalancer", LoadBalancerArgs.builder()        
            .ipId(mainIP.id())
            .type("LB-S")
            .privateNetworks(            
                LoadBalancerPrivateNetworkArgs.builder()
                    .privateNetworkId(mainPrivateNetwork.id())
                    .staticConfig("172.16.0.100")
                    .build(),
                LoadBalancerPrivateNetworkArgs.builder()
                    .privateNetworkId(mainPrivateNetwork.id())
                    .dhcpConfig(true)
                    .build())
            .build(), CustomResourceOptions.builder()
                .dependsOn(mainPublicGateway)
                .build());

    }
}
