const main = new scaleway.tem.Domain("main", {acceptTos: true});
const spf = new scaleway.dns.Record("spf", {
    dnsZone: domainName,
    type: "TXT",
    data: pulumi.interpolate`v=spf1 ${main.spfConfig} -all`,
});
const dkim = new scaleway.dns.Record("dkim", {
    dnsZone: domainName,
    type: "TXT",
    data: main.dkimConfig,
});
const mx = new scaleway.dns.Record("mx", {
    dnsZone: domainName,
    type: "MX",
    data: ".",
});
